import React, { useRef, useState, useEffect } from "react";
import Moveable from "react-moveable";

const App = () => {
  const [moveableComponents, setMoveableComponents] = useState([]);
  const [selected, setSelected] = useState(null);
  const addMoveable = async () => {
    const COLORS = ["red", "blue", "yellow", "green", "purple"];

    const response = await fetch('https://jsonplaceholder.typicode.com/photos/' + Math.floor(Math.random() * 100) + 1);
    const data = await response.json();

    setMoveableComponents([
      ...moveableComponents,
      {
        id: Math.floor(Math.random() * Date.now()),
        top: 0,
        left: 0,
        width: 100,
        height: 100,
        backgroundImage: `url(${data.url})`,
        updateEnd: true
      }
    ]);
  };



  const updateMoveable = (id, newComponent, updateEnd = false) => {
    const updatedMoveables = moveableComponents.map((moveable, i) => {
      if (moveable.id === id) {
        return { id, ...newComponent, updateEnd };
      }
      return moveable;
    });
    setMoveableComponents(updatedMoveables);
  };

  const handleResizeStart = (index, e) => {
    console.log("e", e.direction);
    // Check if the resize is coming from the left handle
    const [handlePosX, handlePosY] = e.direction;
    // 0 => center
    // -1 => top or left
    // 1 => bottom or right

    // -1, -1
    // -1, 0
    // -1, 1
    if (handlePosX === -1) {
      console.log("width", moveableComponents, e);
      // Save the initial left and width values of the moveable component
      const initialLeft = e.left;
      const initialWidth = e.width;

      // Set up the onResize event handler to update the left value based on the change in width
      e.setOrigin(["%", "%"]).on("resize", ({ width }) => {
        const newLeft = initialLeft - (width - initialWidth) / 2;
        updateMoveable(e.target.id, { left: newLeft, width }, false);
      });

      // Set up the onResizeEnd event handler to update the left value based on the change in width and remove the onResize event listener
      e.on("resizeEnd", ({ width }) => {
        const newLeft = initialLeft - (width - initialWidth) / 2;
        updateMoveable(e.target.id, { left: newLeft, width }, true);
        e.off("resize");
      });
    }
  };

  return (
    <main style={{ height : "100vh", width: "100vw" }}>
      <button onClick={addMoveable}>Add Moveable1</button>
      <div
        id="parent"
        style={{
          position: "relative",
          background: "black",
          height: "80vh",
          width: "80vw",
        }}
      >
        {moveableComponents.map((item, index) => (
          <Component
            {...item}
            key={index}
            updateMoveable={updateMoveable}
            handleResizeStart={handleResizeStart}
            setSelected={setSelected}
            isSelected={selected === item.id}
          />
        ))}
      </div>
    </main>
  );
};

export default App;

const Component = ({
  updateMoveable,
  top,
  left,
  width,
  height,
  index,
  color,
  id,
  setSelected,
  isSelected = false,
  updateEnd,
  backgroundImage
}) => {
  const ref = useRef();

  const [nodoReferencia, setNodoReferencia] = useState({
    top,
    left,
    width,
    height,
    index,
    color,
    id,
  });

  let parent = document.getElementById("parent");
  let parentBounds = parent?.getBoundingClientRect();
  const onResize = async (e) => {
    // ACTUALIZAR ALTO Y ANCHO
    let newWidth = e.width;
    let newHeight = e.height;

    const positionMaxTop = top + newHeight;
    const positionMaxLeft = left + newWidth;

    if (parentBounds?.height && positionMaxTop > parentBounds?.height)
      newHeight = parentBounds?.height - top;
    if (parentBounds?.width && positionMaxLeft > parentBounds?.width)
      newWidth = parentBounds?.width - left;

    updateMoveable(id, {
      top,
      left,
      width: newWidth,
      height: newHeight,
      color,
    });

    // ACTUALIZAR NODO REFERENCIA
    const beforeTranslate = e.drag.beforeTranslate;

    ref.current.style.width = `${e.width}px`;
    ref.current.style.height = `${e.height}px`;

    let translateX = beforeTranslate[0];
    let translateY = beforeTranslate[1];

    ref.current.style.transform = `translate(${translateX}px, ${translateY}px)`;

    setNodoReferencia({
      ...nodoReferencia,
      translateX,
      translateY,
      top: top + translateY < 0 ? 0 : top + translateY,
      left: left + translateX < 0 ? 0 : left + translateX,
    });
  };


  const onResizeEnd = async (e) => {
    let newWidth = e.lastRect.width;
    let newHeight = e.lastRect.height;
    updateMoveable(id, { width: newWidth, height: newHeight }, true);
  };

  const onClick = (e) => {
    setSelected(id);
    e.stopPropagation();
  };

  return (
    <div
      className={`component ${isSelected ? "selected" : ""}`}
      style={{
        top: `${top}px`,
        left: `${left}px`,
        width: `${width}px`,
        height: `${height}px`,
        backgroundSize: "cover",
        backgroundImage: `url(${backgroundImage})`, // Establecer la imagen de fondo
      }}
      ref={ref}
      onClick={onClick}
    >
      {isSelected && (
        <Moveable
          target={ref.current}
          resizable={true}
          onResizeStart={(e) => updateMoveable(id, { updateEnd: false })}
          onResize={onResize}
          onResizeEnd={onResizeEnd}
          renderDirections={["nw", "ne", "sw", "se"]}
          edge={false}
          origin={false}
        />
      )}
    </div>
  );
};
