import React, { useRef, useState, useEffect } from "react";
import Moveable from "react-moveable";

const App = () => {
  const [moveableComponents, setMoveableComponents] = useState([]);
  const [selected, setSelected] = useState(null);
  const addMoveable = async () => {
    const COLORS = ["red", "blue", "yellow", "green", "purple"];

    const response = await fetch('https://jsonplaceholder.typicode.com/photos/' + Math.floor(Math.random() * 100) + 1);
    const data = await response.json();

    setMoveableComponents([
    ...moveableComponents,
    {
      id: Math.floor(Math.random() * Date.now()),
      top: 0,
      left: 0,
      width: 100,
      height: 100,
      backgroundImage: `url(${data.url})`,
      updateEnd: true
    }
    ]);
  };



  const updateMoveable = (id, newComponent, updateEnd = false) => {
    const updatedMoveables = moveableComponents.map((moveable, i) => {
      if (moveable.id === id) {
        return { id, ...newComponent, updateEnd };
      }
      return moveable;
    });
    setMoveableComponents(updatedMoveables);
  };
const handleResizeStart = (index, e) => {
  console.log("e", e.direction);
  // Check if the resize is coming from the left handle
  const [handlePosX, handlePosY] = e.direction;
  // 0 => center
  // -1 => top or left
  // 1 => bottom or right

  if (handlePosX === -1 || handlePosX === 0 || handlePosX === 1) {
    console.log("width", moveableComponents, e);
    // Save the initial position and size of the moveable component
    const initialTop = e.top;
    const initialLeft = e.left;
    const initialWidth = e.width;
    const initialHeight = e.height;

    // Set up the onResize event handler to update the position and size based on the changes
    e.setOrigin(["%", "%"]).on("resize", ({ width, height }) => {
      let newTop = initialTop;
      let newLeft = initialLeft;
      let newWidth = width;
      let newHeight = height;

      // Check if the moveable component is outside of the parent container
      if (parentBounds) {
        if (newTop + newHeight > parentBounds.height) {
          newTop = parentBounds.height - newHeight;
        }
        if (newLeft + newWidth > parentBounds.width) {
          newLeft = parentBounds.width - newWidth;
        }
      }

      // Update the moveable component with the new position and size
      updateMoveable(e.target.id, { top: newTop, left: newLeft, width: newWidth, height: newHeight }, false);
    });

    // Set up the onResizeEnd event handler to update the position and size based on the changes and remove the onResize event listener
    e.on("resizeEnd", ({ width, height }) => {
      let newTop = initialTop;
      let newLeft = initialLeft;
      let newWidth = width;
      let newHeight = height;

      // Check if the moveable component is outside of the parent container
      if (parentBounds) {
        if (newTop + newHeight > parentBounds.height) {
          newTop = parentBounds.height - newHeight;
        }
        if (newLeft + newWidth > parentBounds.width) {
          newLeft = parentBounds.width - newWidth;
        }
      }

      // Update the moveable component with the new position and size and remove the onResize event listener
      updateMoveable(e.target.id, { top: newTop, left: newLeft, width: newWidth, height: newHeight }, true);
      e.off("resize");
    });
  }
};


  return (
    <main style={{ height : "100vh", width: "100vw" }}>
      <button onClick={addMoveable}>Add Moveable1</button>
      <div
        id="parent"
        style={{
          position: "relative",
          background: "black",
          height: "80vh",
          width: "80vw",
        }}
      >
        {moveableComponents.map((item, index) => (
          <Component
            {...item}
            key={index}
            updateMoveable={updateMoveable}
            handleResizeStart={handleResizeStart}
            setSelected={setSelected}
            isSelected={selected === item.id}
          />
        ))}
      </div>
    </main>
  );
};

export default App;

const Component = ({
  updateMoveable,
  top,
  left,
  width,
  height,
  index,
  color,
  id,
  setSelected,
  isSelected = false,
  updateEnd,
}) => {
  const ref = useRef();

  const [nodoReferencia, setNodoReferencia] = useState({
    top,
    left,
    width,
    height,
    index,
    color,
    id,
  });

  let parent = document.getElementById("parent");
  let parentBounds = parent?.getBoundingClientRect();

  const onResize = async (e) => {
    // ACTUALIZAR ALTO Y ANCHO
    let newWidth = e.width;
    let newHeight = e.height;

    const positionMaxTop = top + newHeight;
    const positionMaxLeft = left + newWidth;

    if (parentBounds?.height && positionMaxTop > parentBounds?.height)
      newHeight = parentBounds?.height - top;
    if (parentBounds?.width && positionMaxLeft > parentBounds?.width)
      newWidth = parentBounds?.width - left;

    updateMoveable(id, {
      top,
      left,
      width: newWidth,
      height: newHeight,
      color,
    });

    // ACTUALIZAR NODO REFERENCIA
    const beforeTranslate = e.drag.beforeTranslate;

    ref.current.style.width = `${newWidth}px`;
    ref.current.style.height = `${newHeight}px`;

    let translateX = beforeTranslate[0];
    let translateY = beforeTranslate[1];

    // Verificar si el cuadro está fuera de los límites del div padre
    const newTop = top + translateY < 0 ? 0 : top + translateY;
    const newLeft = left + translateX < 0 ? 0 : left + translateX;
    if (
      parentBounds &&
      newTop + newHeight > parentBounds.height &&
      parentBounds.height >= newHeight
    ) {
      translateY = parentBounds.height - top - newHeight;
    }
    if (
      parentBounds &&
      newLeft + newWidth > parentBounds.width &&
      parentBounds.width >= newWidth
    ) {
      translateX = parentBounds.width - left - newWidth;
    }

    ref.current.style.transform = `translate(${translateX}px, ${translateY}px)`;

    setNodoReferencia({
      ...nodoReferencia,
      translateX,
      translateY,
      top: newTop,
      left: newLeft,
    });
  };

  const onDrag = (e) => {
    // ACTUALIZAR POSICIÓN
    let newTop = top + e.drag.beforeTranslate[1];
    let newLeft = left + e.drag.beforeTranslate[0];

    // VERIFICAR LÍMITES DEL DIV PADRE
    const parentBounds = parent?.getBoundingClientRect();
    if (parentBounds) {
      const maxTop = parentBounds.height - height;
      const maxLeft = parentBounds.width - width;
      if (newTop < 0) {
        newTop = 0;
      } else if (newTop > maxTop) {
        newTop = maxTop;
      }
      if (newLeft < 0) {
        newLeft = 0;
      } else if (newLeft > maxLeft) {
        newLeft = maxLeft;
      }
    }

    updateMoveable(id, {
      top: newTop,
      left: newLeft,
      width,
      height,
      color,
    });

    setNodoReferencia({
      ...nodoReferencia,
      translateX: 0,
      translateY: 0,
      top: newTop,
      left: newLeft,
    });
  };

const onResizeEnd = async (e) => {
  let newWidth = e.lastEvent?.width;
  let newHeight = e.lastEvent?.height;

  const positionMaxTop = top + newHeight;
  const positionMaxLeft = left + newWidth;

  // Validar que el componente no se salga del contenedor
  if (parentBounds) {
    if (positionMaxTop > parentBounds?.height) {
      newHeight = parentBounds?.height - top;
    }
    if (positionMaxLeft > parentBounds?.width) {
      newWidth = parentBounds?.width - left;
    }
  }

  const { lastEvent } = e;
  const { drag } = lastEvent;
  const { beforeTranslate } = drag;

  let absoluteTop = top + beforeTranslate[1];
  let absoluteLeft = left + beforeTranslate[0];

  // Validar que el componente no se salga del contenedor
  if (parentBounds) {
    if (absoluteTop < 0) {
      absoluteTop = 0;
    } else if (absoluteTop + newHeight > parentBounds.height && parentBounds.height >= newHeight) {
      absoluteTop = parentBounds.height - newHeight;
    }

    if (absoluteLeft < 0) {
      absoluteLeft = 0;
    } else if (absoluteLeft + newWidth > parentBounds.width && parentBounds.width >= newWidth) {
      absoluteLeft = parentBounds.width - newWidth;
    }
  }

  updateMoveable(
    id,
    {
      top: absoluteTop,
      left: absoluteLeft,
      width: newWidth,
      height: newHeight,
      color,
    },
    true
  );
};


  return (
    <>
      <div
        ref={ref}
        className="draggable"
        id={"component-" + id}
        style={{
          position: "absolute",
          top: top,
          left: left,
          width: width,
          height: height,
          background: color,
        }}
        onClick={() => setSelected(id)}
      />

<Moveable
  target={isSelected && ref.current}
  resizable
  draggable
  onDrag={onDrag}
  onResize={onResize}
  onResizeEnd={onResizeEnd}
  keepRatio={false}
  throttleResize={1}
  renderDirections={["nw", "n", "ne", "w", "e", "sw", "s", "se"]}
  edge={false}
  zoom={1}
  origin={false}
  padding={{ left: 0, top: 0, right: 0, bottom: 0 }}
/>

    </>
  );
};
